public class Division implements Operaciones {
public int operaciones(int n1, int n2) {
		// TODO Auto-generated method stub
		return n1/n2;
	}

/**
 * Método para la division	
 * @param n1 tipo float
 * @param n2 tipo float
 */
	
	@Override
	public float operaciones2(float n1, float n2) {
		// TODO Auto-generated method stub
		return n1/n2;
	}

}
